mapper.select:
SELECT

FROM ai_chat
WHERE deleted = 0

    AND user_id = #{chat.userId}


    AND customer_id = #{chat.customerId}


    AND message_type = #{chat.messageType}


    AND session_id = #{chat.sessionId}

ORDER BY create_time DESC

