mapper.insert:
INSERT INTO customer_account (
    customer_id, username, password, nickname, avatar, bio, 
    phone, email, status, last_login_time, create_time, update_time
) VALUES (
    #{customerId}, #{username}, #{password}, #{nickname}, #{avatar}, #{bio},
    #{phone}, #{email}, #{status}, #{lastLoginTime}, #{createTime}, #{updateTime}
)

mapper.select:
SELECT COUNT(*) FROM social_like sl 
JOIN social_post sp ON sl.target_id = sp.id AND sl.target_type = 1
WHERE sp.customer_account_id = #{userId}
