mapper.select:
SELECT * FROM social_like 
WHERE target_type = #{targetType} 
AND target_id = #{targetId} 
AND customer_account_id = #{customerAccountId}

mapper.insert:
INSERT INTO social_like (
    target_type, target_id, customer_account_id, customer_id, create_time
) VALUES (
    #{targetType}, #{targetId}, #{customerAccountId}, #{customerId}, #{createTime}
)

mapper.delete:
DELETE FROM social_like 
WHERE target_type = #{targetType} 
AND target_id = #{targetId} 
AND customer_account_id = #{customerAccountId}
